import React, { Component } from "react";
import CardProducts from "./cardProducts";
import datas from "../../data";
import product from "./cardProducts";

class productsScreen extends Component {
  state = {
    arrayProducts: datas.products,
  };

  filters = {
    categories: [],
    size: null,
    brand: null,
    color: null,
    price: null,
    data: null,
  };

  splice = (array, value) => {
    const index = array.indexOf(value);
    array.splice(index, 1);
  };

  changeProductCategories = (filter, value) => {
    switch (filter) {
      case "categories":
        if (
          this.filters[filter].includes(value) &&
          this.filters[filter].length == 1
        ) {
          this.splice(this.filters[filter], value);
          this.resetState();
          this.filters.data = datas.products;
        } else if (
          this.filters[filter].includes(value) &&
          this.filters[filter].length > 1
        ) {
          this.splice(this.filters[filter], value);
          this.findItems(this.filters[filter], filter);
        } else {
          this.filters[filter].push(value);
          this.findItems(this.filters[filter], filter);
        }
        break;

      case "size":
        this.filters[filter] = value;

        let newState;

        if (this.filters.data) {
          newState = [...this.filters.data];
        } else {
          newState = [...datas.products];
        }

        newState.forEach((e) => {
          let result = this.checkQuantity(e, filter, value);
          if (!result.includes(true)) {
            this.splice(newState, e);
          }
        });
        this.setState({ arrayProducts: newState });
        break;

      case "brand":
        this.filters[filter] = value;
        this.findItems(Array(this.filters[filter]), filter);
        break;
      case "color":
        this.filters[filter] = value;
        break;
      case "price":
        this.filters[filter] = value;
        this.findItems(Array(this.filters[filter]), filter);
        break;
    }
  };

  checkQuantity = (e, filter, value) => {
    let ProductColorOption, result;
    result = e.colors.map((el) => {
      ProductColorOption = el.sizes.filter(
        (el) => el[filter] == value.toLowerCase()
      );
      if (ProductColorOption[0].quantity) {
        return true;
      }
    });
    return result;
  };

  resetState = () => {
    let products;
    products = datas.products;
    this.setState({ arrayProducts: products });
  };

  findItems = (filters, value) => {
    let arrayofproduct = [];

    if (
      this.filters.categories.length > 0 &&
      this.filters.price &&
      this.filters.brand
    ) {
      this.filters.categories.map((categorie) =>
        arrayofproduct.push(
          datas.products.filter((product) => (product.categories = categorie))
        )
      );
      arrayofproduct.filters(
        (product) =>
          product.price <= this.filter.price &&
          product.price == this.filter.brand
      );
    } else if (this.filters.categories.length > 0 && this.filters.price) {
      this.filters.categories.map((categorie) =>
        arrayofproduct.push(
          datas.products.filter((product) => (product.categories = categorie))
        )
      );
      arrayofproduct.filters((product) => product.price <= this.filters.price);
    } else if (this.filters.categories.length > 0 && this.filters.brand) {
      this.filters.categories.map((categorie) =>
        arrayofproduct.push(
          datas.products.filter((product) => (product.categories = categorie))
        )
      );
      arrayofproduct.filters((product) => product.brand == this.filters.brand);
    } else if ((this.filters.categories.length = 0 && this.filters.brand)) {
      this.filters.band.map((brand) =>
        arrayofproduct.push(
          datas.products.filter((product) => (product.brand = brand))
        )
      );
    } else if ((this.filters.categories.length = 0 && this.filters.price)) {
      this.filters.band.map((price) =>
        arrayofproduct.push(
          datas.products.filter((product) => product.price <= price)
        )
      );
    } else {
      this.filters.categories.map((categorie) =>
        arrayofproduct.push(
          datas.products.filter((product) => (product.categories = categorie))
        )
      );
    }

    const products = [].concat(...arrayofproduct);
    this.setState({ arrayProducts: products });
    this.filters.datas = products;

    // if (this.filters.brands.length > 0 && value != "categories") {
    //   this.filters.categories.map((e) => {
    //     arrayofproduct = datas.products.filter((x) => {
    //       return x.categories == e;
    //     });
    //   });
    //   arrayofproduct = arrayofproduct.filter((x) => {
    //     return x[value] == filters;
    //   });
    // } else if (
    //   value == "categories" &&
    //   this.filters.brand &&
    //   this.filters.price
    // ) {
    //   filters.map((e) => {
    //     arrayofproduct.push(
    //       datas.products.filter((x) => {
    //         return x[value] == e;
    //       })
    //     );
    //   });

    //   arrayofproduct = arrayofproduct.filter((x) => {
    //     return x.brand == this.filters.brand && x.price < this.filters.price;
    //   });
    // } else if (value == "categories" && this.filters.brand) {
    //   filters.map((e) => {
    //     arrayofproduct.push(
    //       datas.products.filter((x) => {
    //         return x[value] == e;
    //       })
    //     );
    //   });
    //   arrayofproduct = arrayofproduct.filter((x) => {
    //     return x.brand == this.filters.brand;
    //   });
    // } else if (
    //   value == "categories" &&
    //   this.filters.categories.length == 0 &&
    //   this.filters.brand
    // ) {
    //   filters.map((e) => {
    //     arrayofproduct.push(
    //       datas.products.filter((x) => {
    //         return x[value] == e;
    //       })
    //     );
    //   });
    // } else {
    //   filters.map((e) => {
    //     arrayofproduct.push(
    //       datas.products.filter((x) => {
    //         return x[value] == e;
    //       })
    //     );
    //   });
    // }
  };

  changeState = (newState) => {};

  render() {
    return (
      <div>
        <section className="filterbar">
          <form>
            <div className="Searchbar">
              <input type="text" label="What are you looking for ?" />
              <i className="fas fa-search"></i>
            </div>
          </form>
          <ul
            className="filters"
            data-filter="categories"
            onClick={(e) => {
              console.log();
              this.changeProductCategories(
                e.target.parentElement.dataset.filter,
                e.target.innerText
              );
            }}
          >
            <li>Robe</li>
            <li>Jupe</li>
            <li>Tee-shirt</li>
            <li>Sweatshirt</li>
            <li>Pants</li>
            <li>Jean</li>
            <li>Vest</li>
            <li>overalls</li>
          </ul>

          <div className="under-filters">
            <form className="under-filters-form">
              <select
                type="select"
                data-filter="size"
                onChange={(e) =>
                  this.changeProductCategories(
                    e.target.dataset.filter,
                    e.target.value
                  )
                }
              >
                <option>S</option>
                <option>M</option>
                <option>XL</option>
              </select>
              <select
                type="select"
                data-filter="brand"
                onChange={(e) =>
                  this.changeProductCategories(
                    e.target.dataset.filter,
                    e.target.value
                  )
                }
              >
                <option>Adidas</option>
                <option>Nike</option>
                <option>Vans</option>
                <option>Veja</option>
                <option>Converse</option>
              </select>
              <select
                type="select"
                onChange={(e) => this.changeProductCategories(e.target.value)}
              >
                <option>20 €</option>
                <option>40 €</option>
                <option>60 €</option>
                <option>80 €</option>
                <option>100 €</option>
                <option>150 €</option>
              </select>
              <select
                type="select"
                onChange={(e) => this.changeProductCategories(e.target.value)}
              >
                <option>Blue</option>
                <option>Red</option>
                <option>Yellow</option>
                <option>Orange</option>
                <option>Green</option>
                <option>Black</option>
              </select>
            </form>
          </div>
        </section>
        <div className="main-products-screen">
          <div className="products-display">
            {this.state.arrayProducts.map((product) => (
              <CardProducts product={product} key={product.id} />
            ))}
          </div>
        </div>
